mapper.select:
select * 
  from user 
 where nickname=#{nickname}

   and email = #{email}


   and password = #{password}


select case when user_id = '' then '불일치' else '일치' end as password
   from (
select ifnull(max(user_id), '') as user_id
  from user 
  where email=#{email} and password=#{password}
  ) a
