mapper.update:
update find_board 
   set bookmark_count = bookmark_count + 1 
 where find_board_num = #{find_board_num}

mapper.delete:
delete from bookmark 
 where find_board_num = #{findboardNum} 
   and email = #{email}

mapper.select:
select bookmark_count 
  from find_board 
 where find_board_num = #{find_board_num}

mapper.update:
